using Microsoft.Xna.Framework;
using Microsoft.Xna.Framework.Graphics;
using Smoke.Sprites;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Smoke.Map;

public class Map
{
    const int TileWidth = 32, TileHeight = 32;

    // Tiles
    private readonly SpriteSheet _tileSet;
    private Dictionary<(int, int), SpriteFrame?> _background = new();
    private Dictionary<(int, int), SpriteFrame?> _shadows = new();
    private Dictionary<(int, int), SpriteFrame?> _foreground = new();

    public Map(SpriteSheet tileSet)
    {
        _tileSet = tileSet;
        Load(_background, _backgroundRaw);
        Load(_foreground, _foregroundRaw);

        CollisionPoints = GetCollisionPoints().ToList();
        InferShadows();
    }

    public void DrawMap(SpriteBatch spriteBatch, Rectangle viewPort)
    {
        // Draw
        Draw(spriteBatch, _background, viewPort);
        Draw(spriteBatch, _shadows, viewPort);
        Draw(spriteBatch, _foreground, viewPort);
    }

    private void Load(Dictionary<(int, int), SpriteFrame?> dictionary, string data)
    {
        var rows = data.Split("\r\n");
        if (Rows == 0) Rows = rows.Length;
        for (int y = 0; y < rows.Length; y++)
        {
            var row = rows[y];
            var columns = row.Split(',');
            if (Columns == 0) Columns = columns.Length;
            for (int x = 0; x < columns.Length; x++)
            {
                string cell = columns[x];
                SpriteFrame? t = cell switch
                {
                    "2" => _tileSet.Get("Tile"),
                    "1" => _tileSet.Get("Wall"),
                    _ => null
                };
                dictionary.Add((x, y), t);
            }
        }
    }

    private void InferShadows()
    {
        var unified = _background.Merge(_foreground);
        var dimenions = unified.Dimensions();
        for (int y = 0; y <= dimenions.Y; y++)
        {
            for (int x = 0; x <= dimenions.X; x++)
            {
                var tile = unified.GetValueOrDefault(x, y);
                if (tile?.Name == "Wall")
                {
                    var matrix = unified.GetShadowMatrix(x, y);

                    // Right Wall
                    if (matrix.IsMatch(_patternShadowRight)) _shadows.Add((x + 1, y), _tileSet.Get("Wall-Right"));
                    if (matrix.IsMatch(_patternShadowTopRight)) _shadows.Add((x + 1, y), _tileSet.Get("Wall-Top-Right"));
                    if (matrix.IsMatch(_patternShadowBottom)) _shadows.Add((x, y + 1), _tileSet.Get("Wall-Bottom"));
                    if (matrix.IsMatch(_patternShadowBottomLeft)) _shadows.Add((x, y + 1), _tileSet.Get("Wall-Bottom-Left"));
                    if (matrix.IsMatch(_patternShadowBottomRight)) _shadows.Add((x + 1, y + 1), _tileSet.Get("Wall-Bottom-Right"));
                    if (matrix.IsMatch(_patternShadowBottomCorner)) _shadows.Add((x + 1, y), _tileSet.Get("Wall-Bottom-Right-Corner"));
                }
            }
        }
    }

    private IEnumerable<Rectangle> GetCollisionPoints()
    {
        foreach (var wall in _foreground.ToList())
        {
            if (wall.Value is not null)
            {
                yield return TilePosToMapRectangle(wall.Key.Item1, wall.Key.Item2);
            }
        }
    }

    public int Rows { get; set; } = 0;
    public int Columns { get; set; } = 0;
    public List<Rectangle> CollisionPoints { get; init; }
    private Point TileSize { get => new Point(TileWidth, TileHeight); }

    #region Draw Helpers

    private void Draw(SpriteBatch _spriteBatch,
                      Dictionary<(int, int), SpriteFrame?> dictionary,
                      Rectangle viewPort)
    {
        foreach (var key in dictionary.Keys.ToList())
        {
            var mapPos = TilePosToMapPos(key.Item1, key.Item2);
            var screenPos = MapPositionToScreenPosition(mapPos, viewPort);

            var tile = dictionary.GetValueOrDefault(key.Item1, key.Item2);
            {
                if (tile is not null)
                {
                    _spriteBatch.Draw(tile.Texture, screenPos, tile.SourceRectangle, Color.White);
                }
            }
        }
    }

    private Vector2 TilePosToMapPos(int x, int y) =>
        new Vector2(x * TileWidth, y * TileHeight);

    private Rectangle TilePosToMapRectangle(int x, int y) =>
        new Rectangle(TilePosToMapPos(x, y).ToPoint(), TileSize);

    private Vector2 MapPositionToScreenPosition(Vector2 mapPosition, Rectangle viewPort)
    {
        Vector2 rocketMiddle = new Vector2(12, 62);
        var viewPortMapTopLeft = viewPort.Location.ToVector2();
        return mapPosition - viewPortMapTopLeft - rocketMiddle;
    }

