public static class MapHelper
{
    public static Dictionary<(int, int), SpriteFrame?> Merge(this Dictionary<(int, int), SpriteFrame?> d1, Dictionary<(int, int), SpriteFrame?> d2)
    {
        var dimA = d1.Dimensions();
        var dimB = d2.Dimensions();
        var dimensions = new Point(Math.Max(dimA.X, dimB.X), Math.Max(dimA.Y, dimB.Y));

        var d3 = new Dictionary<(int, int), SpriteFrame?>();
        for (int y = 0; y <= dimensions.Y; y++)
        {
            for (int x = 0; x <= dimensions.X; x++)
            {
                var b = d2.GetValueOrDefault(x, y);
                var a = d1.GetValueOrDefault(x, y);
                var c = b is null ? a : b;
                d3.Add((x, y), c);
            }
        }
        return d3;
    }

    public static Point Dimensions(this Dictionary<(int, int), SpriteFrame?> layer)
    {
        var keys = layer.Keys.ToList();
        var columns = keys.Select(x => x.Item1).Max();
        var rows = keys.Select(x => x.Item2).Max();
        return new Point(columns, rows);
    }

    public static SpriteFrame? GetValueOrDefault(this Dictionary<(int, int), SpriteFrame?> dict, int x, int y) =>
        dict.GetValueOrDefault((x, y));

    public static ShadowMatrix GetShadowMatrix(this Dictionary<(int, int), SpriteFrame?> dict, int x, int y)
    {
        var sb = new StringBuilder();
        for (int dy = -1; dy <= 1; dy++)
        {
            for (int dx = -1; dx <= 1; dx++)
            {
                var symbol = dict.GetValueOrDefault(x + dx, y + dy)
                                 .TileToSymbol();
                sb.Append(symbol);
            }
        }
        return new ShadowMatrix(sb.ToString());

    }

    public static string? TileToSymbol(this SpriteFrame? t) =>
        t?.Name switch
        {
            "Wall" => "#",
            "Tile" => ".",
            _ => " "
        };
}

public record ShadowMatrix
{
    public ShadowMatrix(string matrixString)
    {
        MatrixString = matrixString;
    }

    public string MatrixString { get; init; }

    public bool IsMatch(string mask)
    {
        if (mask.Length != MatrixString.Length) return false;
        for (int i = 0; i < mask.Length; i++)
        {
            char maskChar = mask[i];
            if (maskChar == '*') continue;
            if (MatrixString[i] != maskChar) return false;
        }
        return true;
    }
}


